$(function(){
    $('#itxdebito').mask("00,00%", {reverse: true});
    $('#idiasdebito').mask("00");
    $('#itxcredcom').mask("00,00%", {reverse: true});
    $('#idiascredcom').mask("00");
    $('#idiasantecip').mask("00");
    for(var i=1;i<=12;i++){
        $('#itxantecip_'+i+'').mask("00,00%", {reverse: true});
        $('#itxcredsemjuros_'+(12+i)+'').mask("00,00%", {reverse: true});
    }    
   
    
    $('#inome').on('blur',function(){
        var nome = $(this).val();
        var action = "ConfereNomeAdm";
        if($(this).val() != ''){
            $.ajax({
                url:baselink+"/ajax/"+action,
                type: 'POST',
                data:{q:nome},
                dataType:'json',
                success:function(json){
                    if(json.length > 0){
                       $('#inome').val("");
                       alert("O nome da Admnistradora já existe. Tente outro.");
                    }
                }
            });
        }
    });  
});



   
    
