function filterColumn ( i ) {
    
    if(i != ''){
       $('#tabelafornecedores').DataTable().column( i ).search(
            $('#ifiltro').val()
        ).draw();
    }else{
        $('#tabelafornecedores').DataTable().search(
            $('#ifiltro').val()
        ).draw(); 
    }
}

