function filterColumn ( i ) {
    
    if(i != ''){
       $('#tabelafuncionarios').DataTable().column( i ).search(
            $('#ifiltro').val()
        ).draw();
    }else{
        $('#tabelafuncionarios').DataTable().search(
            $('#ifiltro').val()
        ).draw(); 
    }
}

