function limparPreenchimento(){
    
    var date = new Date();
    var day = date.getDate();
    var month = date.getMonth() + 1;
    var year = date.getFullYear();
    if (month < 10) month = "0" + month;
    if (day < 10) day = "0" + day;
    var today = year + "-" + month + "-" + day;       
      
    $("#idtop").focus();
    $("#idtop").val(today);
    
    $('#inropedido').val("");
    $('#inropedido').blur();
    if($('#idespesa').is(':checked')){
        $('#ireceita').click();
        $('#idespesa').click();
    }else{
        $('#idespesa').click();
        $('#ireceita').click();
    }
    $('#idetalhe').val("");
    $('#ifavorecido').val("");
    $('#ivalortotal').val("");
    $('#icc').val("");
    $('#iformapgto').val("");
    $('#iformapgto').change();
    $('#iobserv').val("");

}

